import { Button, TextField, Typography } from '@mui/material';
import React, { useState } from 'react'

const Counter = () => {
    var[name,setname] =useState('0')
    var[change,setchange]= useState()

    const handleinput=(e)=>{
        console.log(e.target.value)
        setname(e.target.value)
    }
    const submithandler=()=>{
        console.log("Clicked")
        setchange(name)
    }
  return (
    <div>
        <Typography variant='h4'>{name}</Typography>
        <TextField >onChange={handleinput}</TextField>
        <Button variant='contained' color='success' onClick={handleinput}>+</Button>
        <Button variant='contained' color='success' onClick={submithandler}>-</Button>
    </div>
  )
}

export default Counter
